protected func ControlSpecial2()
  {
  var i, pBase;
  while (pBase=FindBase(GetOwner(), i++))
    if (PathFree(GetX(), GetY(), GetX(pBase), GetY(pBase)))
      {
      Message("Ich sehe meine Basis!", this());
      break;
      }
  if (!pBase) Message("Ich sehe meine Basis nicht :/", this());
  }
